import React,{Component} from 'react';
import logo from '../logo.svg';
import './App.css';
import '../Header/header';
import Header from "../Header/header";
import UserReg from "../User/RegisterUser/registerUser"
import PeopleService from '../repository/axiosusersrepository'
import {BrowserRouter as Router,Route} from 'react-router-dom'

import UserService from "../repository/axiosusersrepository";

class App extends Component{

    constructor(props) {
        super();
        this.state = {
            users: [],
        }
    }

    loadUsers = () => {
        UserService.fetchAllUsers().then((data) =>{
            this.setState({
                users : data.data,
            })
        }
        );
    }

    createUser = (user) =>{
        UserService.addUser(user).then((response) =>{
            this.setState((prevState) =>{
                console.log("Users before update");
                console.log(prevState.users);

                console.log("User which is being added");
                console.log(user);

                const users = prevState.users.concat(user);


                return {
                    "users" : users
                }
            })
            }
        )
    }


    render(){
        const routing = (
            <div>
                <Router>
                    <Header/>




                </Router>
            </div>
        )
        return (
            <div className={"App"}>
                {routing}
            </div>
        );
    }

}

export default App;
