import React, {useEffect, useState} from "react";
import {Link, useParams} from "react-router-dom";
import axios from "../../custom-axios/axios";
import LanguageRow from "../../Language/Language Row/languageRow"

const UserProfile = () => {

    const [user, setUser] = useState({});
    const {userID} = useParams();

    useEffect(() => {
        axios.get("/users/" + userID).then((data) => {
            console.log("Inside useEffect",data.data)
            setUser( data.data);
        })
    },[]);
    console.log("Being called inside UserProfile ->",user);
    console.log(typeof user.languages)


    return (
        <section id="model-details">
            <div className="container">
                <div className="row">
                    <div className="col-12 col-md-5">
                        <div className="profile-block escort-bio">
                            <h3 className="profile-block-title font-alt">About my culture</h3>
                            <div id="bio-content" className="bio-container">
                                <p> {user.description}</p>
                            </div>
                        </div>
                    </div>
                    <div className="col-12 col-md-3">
                        <div className="profile-block">
                            <h3>Profile</h3>
                            <ul style={{"list-style-type" : "none"}}>
                                <li><span className="font-400">Name: </span><span
                                    className="profile-right">{user.userName}</span></li>

                                <li>
                                    <span className="font-400">Location: </span>
                                    <span className="profile-right">London</span>
                                </li>
                                <li>
                                    <span className="font-400">Languages: </span>
                                    <span className="profile-right">
                                        <ul>
                                    {user.languages && user.languages.map((item, key) => {
                                        console.log(item)
                                        return <li>{item.name}</li>
                                    })}
                                        </ul>
                                    </span>


                                </li>

                            </ul>
                        </div>
                    </div>
                    <div className="col-12 col-md-5">
                        <div className="profile-block escort-bio">
                            <Link to={"/users/culture/"}>
                                <button className="btn btn-sm btn-outline-dark">
                                    <span><strong>Select  User</strong></span>
                                </button>
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
};


export default UserProfile;
